def TonelliShanks(n, p):

    z = 0
    for i in range(2, p):
        z = pow(i, (p - 1)//2, p)
        if z == p -1:
            z = i
            break

    Q = p - 1
    M = 0
    while Q % 2 == 0:
        Q //= 2
        M += 1
    R = pow(n, (Q + 1)//2, p)
    c = pow(z, Q, p)
    t = pow(n, Q, p)

    while M > 0:
        if t == 1:
            return R
        i = 0
        while pow(t, pow(2, i), p) != 1:
            i += 1
        b = pow(c, pow(2, M-i-1), p)
        M = i
        c = pow(b, 2, p)
        t = (t*c) % p
        R = (R*b) % p


